sys_script_include.script:
var Home = Class.create();
Home.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {
	/**
	 * Get home's room names and list of people found in each
	 * @return {object} keyed on room name with array of occupants
	 */
	getRooms: function () {
		var room = new GlideRecord( 'x_snc_homenow_room' );
		room.query();

		var rooms = {};
		while (room.next()) {
			var peopleStr = room.people.getDisplayValue(),
				people;

			if (peopleStr !== null && peopleStr !== '') {
				people = peopleStr.split('\n');

				/*
				gs.info( 'PEOPLE: "' + people + '". .length: ' + people.length );
 				*/
			}
			rooms[room.name] = people;
		}
		gs.info('Home: '+new global.JSON().encode(rooms));

		return rooms;
	},

	type: 'Home'
});
