sys_ui_script.script:
(function () {
	'use strict';

	var xmHomeNow = angular.module( 'xmHomeNow', []);

	xmHomeNow.constant('HomeConst', {
		refreshRate: 10000 // update view every 10 seconds
	});

	xmHomeNow.service('Home', ['$http', function ($http) {
		this.getRooms = function () {
			return $http({
				method: 'get',
				url: 'x_snc_homenow_get_rooms.do',
				headers: {
					'Content-Type': 'application/json'
				}
			})
			.then(function (response) {
				return response.data;
			});
		};
	}]);

	xmHomeNow.controller('HomeCtrl', ['$scope', '$interval', 'Home', 'HomeConst', function ($scope, $interval, Home, HomeConst) {
		function updateRooms() {
			Home.getRooms()
			.then(function (rooms) {
				$scope.rooms = rooms;
			});
		}
		updateRooms();

		var intervalPromise = $interval(function() {
			updateRooms();
		}, HomeConst.refreshRate);

		$scope.$on('$destroy', function () {
			$interval.cancel(intervalPromise);
		});
	}]);

})();
